class Solution:
    def longestMountain(self, arr: List[int]) -> int:
        n = len(arr)
        max_length = 0
        for i in range(n):
            if i == 0:
                continue
            temp_arr = []
            for j in range(i, n):
                temp_arr.append(arr[j])
                if j == i:
                    continue
                if arr[j] > arr[j-1]:
                    max_val = max(temp_arr)
                    if i != 0 and arr[j-1] > max_val:
                        if arr[i-1] < arr[i]:
                            max_length = max(max_length, len(temp_arr))
                            temp_arr = []
                        else:
                            break
                elif arr[j] < arr[j-1]:
                    max_val = max(temp_arr)
                    if i != 0 and arr[j-1] < max_val:
                        if arr[i-1] > arr[i]:
                            max_length = max(max_length, len(temp_arr))
                            temp_arr = []
                        else:
                            break
        return max_length

efficient_solution:
class Solution:
    def longestMountain(self, arr: List[int]) -> int:
        n = len(arr)
        ans = l = 0
        while l + 2 < n:
            r = l + 1
            if arr[l] < arr[r]:
                while r + 1 < n and arr[r] < arr[r + 1]:
                    r += 1
                if r < n - 1 and arr[r] > arr[r + 1]:
                    while r < n - 1 and arr[r] > arr[r + 1]:
                        r += 1
                    ans = max(ans, r - l + 1)
                else:
                    r += 1
            l = r
        return ans

model_solution:
class Solution:
    def longestMountain(self, arr: List[int]) -> int:
        n = len(arr)
        pre = [0] * n
        ans = 0
        for i in range(1, n):
            if arr[i] > arr[i - 1]:
                pre[i] = pre[i - 1] + 1
            else:
                pre[i] = 0
        for i in range(n - 2, -1, -1):
            if pre[i] == 0 and i + 1 < n and arr[i] < arr[i + 1]:
                ans = max(ans, pre[i + 1] + 1)
            pre[i] += pre[i + 1]
        return ans
