# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def verticalTraversal(self, root: Optional[TreeNode]) -> List[List[int]]:
        if root is None:
            return []
        cols = defaultdict(list)
        queue = deque([(0, root)])
        while queue:
            _, node = queue.popleft()
            if node:
                cols[0].append(node.val)
                queue.append((0, node.left))
                queue.append((0, node.right))
                for _ in range(len(cols)):
                    if cols[_]:
                        queue.append((-_ - 1, cols[_][0]))
        ans = []
        for k in sorted(cols.keys()):
            ans.append([v[0] for v in sorted(cols[k])])
        return ans
