class Solution:
    def maxArea(self, height: List[int]) -> int:
        n = len(height)
        area = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(i, n):
                area[i][j] = area[j][i] = (j - i) * min(height[i], height[j])
        ans = float('-inf')
        for i in range(n):
            for j in range(i, n):
                ans = max(ans, area[i][j])
        return ans
