class Solution:
    def invalidTransactions(self, transactions: List[str]) -> List[str]:
        invalid = []
        for transaction in transactions:
            name, time, amount, city = transaction.split(",")
            time, amount = int(time), int(amount)
            for other_transaction in transactions:
                other_name, other_time, other_amount, other_city = other_transaction.split(",")
                other_time, other_amount = int(other_time), int(other_amount)
                if other_name != name and (amount > 1000 or abs(time - other_time) <= 60 and city != other_city):
                    invalid.append(transaction)
        return invalid
