class Solution:
    def numSmallerByFrequency(self, queries: List[str], words: List[str]) -> List[int]:
        def f(s: str) -> int:
            cnt = {c: s.count(c) for c in set(s)}
            mn = min(cnt.keys())
            return cnt[mn]

        ans = []
        for q in queries:
            cnt = {c: [] for c in set(q)}
            for w in words:
                cnt[f(w)].append(1)
            for c in sorted(cnt.keys()):
                cnt[c] = len(cnt[c])
            ans.append(sum(not c > f(q) for c in cnt.keys()))
        return ans
