class Solution:
    def deleteTreeNodes(self, nodes: int, parent: List[int], value: List[int]) -> int:
        def dfs(i):
            v = value[i]
            g = [[] for _ in range(nodes)]
            m = [0] * nodes
            for j in range(nodes):
                if parent[j] == i:
                    m[j] += 1
                    g[i].append(j)
            s = v
            for j in g[i]:
                t, n = dfs(j)
                s += t
                m[j] += n
            if s == 0 and i != 0:
                m[i] = 0
                s = 0
            return (s, m[i])

        g = [[] for _ in range(nodes)]
        for i in range(1, nodes):
            g[parent[i]].append(i)
        sum_val = [0] * nodes
        for i in range(nodes):
            sum_val[i] = value[i]
        count = [0] * nodes
        for i in range(1, nodes):
            count[i] = 1
        res = [0] * nodes
        for i in range(nodes):
            res[i] = dfs(i)
        return sum(1 for i in range(nodes) if res[i][0] != 0 and count[i] != 0)
