import random

class Solution:
    def sumNumbers(self, root):
        def dfs(root, s):
            if root is None:
                return 0
            s = s * 10 + root.val
            if root.left is None and root.right is None:
                return s
            return dfs(root.left, s) + dfs(root.right, s)

        return dfs(root, 0)

def generate_test_case():
    solution = Solution()

    root = generate_binary_tree(random.randint(1, 10))

    return root

def generate_binary_tree(num_nodes):
    # Generate a binary tree with num_nodes nodes, with values from 0 to 9
    if num_nodes <= 0:
        return None

    # Generate the root node
    root_val = random.randint(0, 9)
    root = TreeNode(root_val)

    # Generate the left subtree
    left_nodes = random.randint(0, num_nodes - 1)
    root.left = generate_binary_tree(left_nodes)

    # Generate the right subtree
    right_nodes = num_nodes - 1 - left_nodes
    root.right = generate_binary_tree(right_nodes)

    return root

def test_generated_test_cases(num_tests):
    test_case_generator_results = []
    for i in range(num_tests):
        root = generate_test_case()
        solution = Solution()
        expected_result = solution.sumNumbers(root)
        test_case = f"assert solution.sumNumbers(decode_tree({encode_tree(root)})) == {expected_result}"
        test_case_generator_results.append(test_case) 
    return test_case_generator_results

def encode_tree(root):
    # Encode a binary tree into a string representation
    if root is None:
        return "None"
    return f"TreeNode({root.val}, {encode_tree(root.left)}, {encode_tree(root.right)})"

def decode_tree(tree_str):
    # Decode a string representation of a binary tree into a TreeNode object
    if tree_str == "None":
        return None
    result = eval(tree_str)
    return result

