canonical_solution:
class Solution:
    def minOperations(self, nums: List[int]) -> int:
        return sum(v.bit_count() for v in nums) + max(0, max(nums).bit_length() - 1)

inefficient_solution:
class Solution:
    def minOperations(self, nums: List[int]) -> int:
        arr = [0] * len(nums)
        operations = 0
        for i, num in enumerate(nums):
            temp_arr = arr[:]
            for _ in range(1 << num.bit_length()):
                for j, val in enumerate(temp_arr):
                    arr[j] = val * 2
                operations += 1
                if arr[i] >= num:
                    break
            else:
                for j, val in enumerate(temp_arr):
                    arr[j] += 1
                operations += 1
        return operations
