            for j in range(100):
                s_list[i][j] = s_list[i - 1][j] if i > 0 else ""
                t_list[i][j] = t_list[i - 1][j] if i > 0 else ""
        for i in range(m):
            for j in range(n):
                if s_list[0][j] == t_list[0][j]:
                    s_list[1][j] = s_list[0][j] + s_list[1][j - 1]
                    t_list[1][j] = t_list[0][j] + t_list[1][j - 1]
                else:
                    s_list[1][j] = s_list[0][j] + s_list[1][j - 1]
                    t_list[1][j] = t_list[0][j] + t_list[1][j - 1]
                if s[i] == t[j]:
                    s_list[0][j] = s_list[0][j - 1] + s[i]
                    t_list[0][j] = t_list[0][j - 1] + t[j]
