class Solution:
    def maxHeight(self, cuboids: List[List[int]]) -> int:
        cuboids_with_rotations = []
        for cuboid in cuboids:
            for _ in range(3):
                cuboids_with_rotations.append(cuboid)
                cuboids_with_rotations[-1][1], cuboids_with_rotations[-1][2] = cuboids_with_rotations[-1][2], cuboids_with_rotations[-1][1]
        cuboids_with_rotations.sort()

        memo = dict()
        def dp(i):
            if i == len(cuboids_with_rotations):
                return 0
            if i in memo:
                return memo[i]
            max_height = 0
            for j in range(i+1, len(cuboids_with_rotations)):
                if (cuboids_with_rotations[i][0] <= cuboids_with_rotations[j][0] and
                    cuboids_with_rotations[i][1] <= cuboids_with_rotations[j][1] and
                    cuboids_with_rotations[i][2] <= cuboids_with_rotations[j][2]):
                    max_height = max(max_height, dp(j))
            memo[i] = max_height + cuboids_with_rotations[i][2]
            return memo[i]

        return max(dp(i) for i in range(len(cuboids_with_rotations)))
