        nums = [[] for _ in range(len(d))]
        for v in arr:
            if v in d:
                nums[d[v]].append(v)

        def lengthOfLIS(nums):
            m = {}
            ans = 0
            for i, num in enumerate(nums, 1):
                m[num] = i
                t = 0
                for j in range(1, i):
                    if nums[j-1] < num:
                        t = max(t, m[nums[j-1]] + 1)
                ans = max(ans, t)
            return ans

        ans = len(target)
        for i, num in enumerate(nums):
            ans -= lengthOfLIS([j for j in nums if j > num])

