class Solution:
    def minSkips(self, dist, speed, hoursBefore):
        n = len(dist)
        maxWaitTime = 0
        for i in range(n):
            maxWaitTime += dist[i] / speed
        if maxWaitTime > hoursBefore:
            return -1

        def checkout(skips):
            waitTime = 0
            timeSpent = 0
            for i, x in enumerate(dist):
                timeSpent += x / speed
                if i < n - 1 and skips > 0 and timeSpent != int(timeSpent):
                    waitTime += 1 - timeSpent % 1
                    timeSpent += 1 - timeSpent % 1
                    skips -= 1
            return timeSpent + waitTime <= hoursBefore

        lo, hi = 0, n
        while lo < hi:
            mid = (lo + hi) // 2
            if checkout(mid):
                hi = mid
            else:
                lo = mid + 1
        return lo
