class Solution:
    def networkBecomesIdle(self, edges: List[List[int]], patience: List[int]) -> int:
        g = {i: [] for i in range(len(patience))}
        for u, v in edges:
            g[u].append(v)
            g[v].append(u)
        q = []
        vis = set()
        ans = d = 0
        for i in range(len(patience)):
            q.append(i)
            vis.add(i)
        while q:
            d += 1
            t = d * 2
            temp_q = []
            for _ in range(len(q)):
                u = q.pop(0)
                for v in g[u]:
                    if v not in vis:
                        temp_q.append(v)
                        vis.add(v)
            q = temp_q
            for v in vis:
                if patience[v] + 1 < t:
                    ans = max(ans, (t - 1 - patience[v]) * patience[v] + t)
        return ans
