class Solution:
    def maxRunTime(self, n: int, batteries: List[int]) -> int:
        import copy
        total = sum(batteries)
        for _ in range(1000000):
            batteries_copy = copy.deepcopy(batteries)
            for i in range(n):
                for j in range(n):
                    if batteries_copy[j] > 0:
                        batteries_copy[j] -= 1
                        batteries_copy[i] = min(batteries_copy[i] + 1, total // n)
                        break
            total -= min(batteries_copy)
            if total < 0:
                return total
        return 0
