class Solution:
    def repeatLimitedString(self, s: str, repeatLimit: int) -> str:
        cnt = [0] * len(s)
        for i, c in enumerate(s):
            cnt[ord(c) - ord('a')] = i
        ans = []
        for i in range(len(s)):
            if s[i] in ans:
                temp = []
                for j in range(len(ans)):
                    if ord(ans[j]) == ord(s[i]):
                        temp.append(ans[j])
                ans = ans[:j] + temp + ans[j+1:]
                temp = []
                for j in range(len(ans)-1, -1, -1):
                    if ord(ans[j]) == ord(s[i]) and len(temp) < repeatLimit:
                        temp.append(ans[j])
                ans = ans[:j+1] + temp + ans[j+2:]
                if len(temp) < repeatLimit:
                    ans = ans[:j+1] + ans[j+1:]
                ans.append(s[i])
            else:
                ans.append(s[i])
        return ''.join(ans)
