class Solution:
    def sortPeople(self, names: List[str], heights: List[int]) -> List[str]:
        heights_dict = {height: name for name, height in zip(names, heights)}
        sorted_heights = sorted(heights, reverse=True)
        sorted_names = []
        for height in sorted_heights:
            sorted_names.append(heights_dict[height])
        return sorted_names
