class Solution:
    def captureForts(self, forts: List[int]) -> int:
        n = len(forts)
        i = ans = 0
        memory = []
        while i < n:
            j = i + 1
            if forts[i]:
                temp_ans = 0
                while j < n and forts[j] == 0:
                    temp_ans += 1
                    j += 1
                if j < n and forts[i] + forts[j] == 0:
                    temp_ans += 1
                    while i < n:
                        memory.append(forts[i])
                        i += 1
                    while temp_ans > 0:
                        if memory[-1] == 0:
                            temp_ans -= 1
                            memory.pop()
                        else:
                            break
                    if temp_ans > 0:
                        ans = max(ans, temp_ans)
            i = j
        return ans
