import random

class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

class Solution:
    def closestValue(self, root: TreeNode, target: float) -> int:
        ans, mi = root.val, float('inf')
        while root:
            t = abs(root.val - target)
            if t < mi or (t == mi and root.val < ans):
                mi = t
                ans = root.val
            if root.val > target:
                root = root.left
            else:
                root = root.right
        return ans


def generate_test_case():
    solution = Solution()

    # Generate random binary search tree
    root = generate_binary_search_tree()

    # Generate a random target value
    target = random.uniform(-10**9, 10**9)

    # Calculate the expected result using the provided Solution class
    expected_result = solution.closestValue(root, target)

    return root, target, expected_result


def generate_binary_search_tree():
    values = random.sample(range(10**9), random.randint(1, 10))
    return construct_binary_search_tree(values)


def construct_binary_search_tree(values):
    if not values:
        return None

    mid = len(values) // 2
    root = TreeNode(values[mid])
    root.left = construct_binary_search_tree(values[:mid])
    root.right = construct_binary_search_tree(values[mid + 1:])

    return root


def test_generated_test_cases(num_tests):
    test_case_generator_results = []
    for i in range(num_tests):
        root, target, expected_result = generate_test_case()
        solution = Solution()
        assert solution.closestValue(root, target) == expected_result
        test_case_generator_results.append(f"assert solution.closestValue({get_tree_string(root)}, {target}) == {expected_result}")
    return test_case_generator_results


def get_tree_string(root):
    if not root:
        return "None"

    left = get_tree_string(root.left)
    right = get_tree_string(root.right)

    return f"TreeNode({root.val}, {left}, {right})"


if __name__ == "__main__":
    num_tests = 100  # You can change this to generate more test cases
    test_case_generator_results = test_generated_test_cases(num_tests)
