class Solution:
    def getWordsInLongestSubsequence(
        self, n: int, words: List[str], groups: List[int]
    ) -> List[str]:
        max_length = 0
        longest_subseq = []

        for i in range(n):
            j = i
            subseq = []
            while j < n and groups[i] != groups[j]:
                subseq.append(words[j])
                j += 1
            if len(subseq) > max_length:
                max_length = len(subseq)
                longest_subseq = subseq

        return longest_subseq
