class Solution:
    def reorganizeString(self, s: str) -> str:
        n = len(s)
        cnt = [0] * 26
        for char in s:
            cnt[ord(char) - ord('a')] += 1
        mx = max(cnt)
        char_list = [[] for _ in range(26)]
        for i, v in enumerate(cnt):
            char_list[i] = [chr(i + ord('a'))] * v
        ans = [''] * n
        for i in range(26):
            while char_list[i]:
                for j in range(n):
                    if ans[j] == '':
                        ans[j] = char_list[i].pop()
                        break
        return ''.join(ans)
