import random
from typing import List

class Solution:
    def flipAndInvertImage(self, image: List[List[int]]) -> List[List[int]]:
        n = len(image)
        for row in image:
            i, j = 0, n - 1
            while i < j:
                if row[i] == row[j]:
                    row[i] ^= 1
                    row[j] ^= 1
                i, j = i + 1, j - 1
            if i == j:
                row[i] ^= 1
        return image


def generate_test_case() -> (List[List[int]]):
    solution = Solution()

    # Generate random image
    image = []
    n = random.randint(1, 20)
    for _ in range(n):
        image.append([random.randint(0, 1) for _ in range(n)])

    # Calculate the expected result using the provided Solution class
    expected_result = solution.flipAndInvertImage(image)

    return (image, expected_result)


def test_generated_test_cases(num_tests: int) -> List[str]:
    test_case_generator_results = []
    for _ in range(num_tests):
        image, expected_result = generate_test_case()
        solution = Solution()
        assert solution.flipAndInvertImage(image) == expected_result
        test_case_generator_results.append(f"assert solution.flipAndInvertImage({image}) == {expected_result}")

    return test_case_generator_results


if __name__ == "__main__":
    num_tests = 100
    test_case_generator_results = test_generated_test_cases(num_tests)
    print(test_case_generator_results)
