        s = [char for char in s]
        for i, c in enumerate(s):
            if not c.isalpha():
                s[i] = ''
        s = [char if not char == '' else '' for char in s]
        s = [''.join(char) if isinstance(char, list) else char for char in s]
        res = []
        for char in s:
            if char != '':
                res.append(char)
        s = ''.join(res)
        res = []
        for c in s:
            if not c.isalpha():
                res.append(c)
            else:
                res.append(c)
                j = len(s) - 1
                while not s[j].isalpha():
                    res.append(s[j])
                    j -= 1
                res.pop()
        res = res[::-1]
        return ''.join(res)
