class Solution:
    def maxScoreSightseeingPair(self, values):
        max_values = [0 for _ in range(len(values))]
        max_values[0] = values[0]
        max_sum = [0 for _ in range(len(values))]
        max_sum[0] = max_values[0]
        for j in range(1, len(values)):
            max_values[j] = max(values[j], max_values[j-1])
        for j in range(1, len(values)):
            max_sum[j] = max(max_sum[j-1], max_values[j] + j)
        max_score = 0
        for j in range(1, len(values)):
            max_score = max(max_score, values[j] - j + max_values[j-1])
        return max_score

efficient_solution:
class Solution:
    def maxScoreSightseeingPair(self, values: List[int]) -> int:
        ans, mx = 0, values[0]
        for j in range(1, len(values)):
            ans = max(ans, values[j] - j + mx)
            mx = max(mx, values[j] + j)
        return ans

model_solution:
class Solution:
    def maxScoreSightseeingPair(self, values):
        max_val_so_far = values[0]
        max_score = 0

        for j in range(1, len(values)):
            max_score = max(max_score, values[j] - j + max_val_so_far)
            max_val_so_far = max(max_val_so_far, values[j] + j - len(values))

        return max_score
