class Solution:
    def minFlips(self, target: str) -> int:
        n = len(target)
        s = ['0'] * n
        ans = 0
        for i in range(n):
            for j in range(i, n):
                if target[j] == '1':
                    s[j] = '1'
                else:
                    s[j] = '0'
            temp = [int(x) for x in s]
            if temp != [int(x) for x in target]:
                ans += 1
                for j in range(i, n):
                    s[j] = '1' if s[j] == '0' else '0'
        return ans

