class Solution:
    def smallestNumber(self, num: int) -> int:
        if num == 0:
            return 0
        neg = num < 0
        num = abs(num)
        digits = []
        while num:
            digits.append(num % 10)
            num //= 10
        digits.sort()
        ans = []
        if not neg and not digits[0]:
            digits.pop(0)
        for i in range(len(digits)):
            if i and not ans:
                ans.append(0)
            ans.append(digits[i])
        if neg:
            ans = [-i for i in ans]
        return int(''.join(map(str, ans)))

efficient_solution:
class Solution:
    def smallestNumber(self, num: int) -> int:
        if num == 0:
            return 0
        cnt = [0] * 10
        neg = num < 0
        num = abs(num)
        while num:
            num, v = divmod(num, 10)
            cnt[v] += 1
        ans = ""
        if neg:
            for i in range(9, -1, -1):
                if cnt[i]:
                    ans += str(i) * cnt[i]
            return -int(ans)
        if cnt[0]:
            for i in range(1, 10):
                if cnt[i]:
                    ans += str(i)
                    cnt[i] -= 1
                    break
        for i in range(10):
            if cnt[i]:
                ans += str(i) * cnt[i]
        return int(ans)

model_solution:
class Solution:
    def smallestNumber(self, num: int) -> int:
        if num == 0:
            return 0
        neg = num < 0
        num = abs(num)
        digits = []
        while num:
            digits.append(num % 10)
            num //= 10
        digits.sort()

        # Find the first non-zero digit
        first_non_zero = 0
        while first_non_zero < len(digits) and digits[first_non_zero] == 0:
            first_non_zero += 1

        if neg:
            digits = [-i for i in digits]
        digits[first_non_zero:] = sorted(digits[first_non_zero:])

        # Add a zero at the beginning if necessary
        if first_non_zero > 0:
            digits.insert(0, 0)

        return int(''.join(map(str, digits)))
