class Solution:
    def scheduleCourse(self, courses):
        courses.sort(key=lambda x: x[1])
        pq = []
        for duration, last in courses:
            pq.append(-duration)
            self.check(pq, duration, last)
        return len(pq)

    def check(self, pq, duration, last):
        s = sum(pq)
        if s > last:
            max_val = max(pq)
            pq.remove(max_val)
            self.check(pq, duration, last)
            pq.append(max_val)

